sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// Add your code here
	gs.info('<business rule : Upload To GitHub> execution starts');
	var files = x_98418_github.getAttachments(current.sys_id);
	if(files.length > 0) {
		current.filescount = files.length;
		for(var i=0; i < files.length; i++) {
			var fileId = files[i].id;
			var fileName = x_98418_github.replaceSpaceWith_(files[i].name);
			var content = files[i].content;
			var folderName = x_98418_github.replaceSpaceWith_(current.folder);

			//call the rest web service to upload the file into github
			var gitEndPoint = 'https://api.github.com/repos/SnowDocs/dumps_materials_docs/contents/' + folderName + '/' + fileName;
			var user = 'snowdocs';
			var password = 'Sn@34306786';
			var reqBody = "{\"message\": \"Initial Commit\", \"committer\": {\"name\": \"" + current.contributor + "\", \"email\": \"" + current.contributorname + "\"},\"content\": \"" + content + "\", \"branch\": \"master\"}";

			try {
				var gitApi = new sn_ws.RESTMessageV2();
				gitApi.setEndpoint(gitEndPoint);
				gitApi.setHttpMethod('PUT');
				gitApi.setBasicAuth(user, password);
				gitApi.setRequestHeader("Accept","application/json");
				gitApi.setRequestHeader('Content-Type','application/json');
				gitApi.setRequestBody(reqBody);
				var response = gitApi.execute();
				var responseBody = response.getBody();
				var httpStatus = response.getStatusCode();
				var parsed = JSON.parse(responseBody);
				if(httpStatus === 201) {
					var file_urls = current.files;
					if(file_urls) {
						current.files = file_urls + ',' + decodeURI(parsed.content.html_url);
					} else {
						current.files = decodeURI(parsed.content.html_url);
					}
				} else {
					current.errorinupload = true;
					current.errormessage = responseBody;
				}
				gs.info('ResponseBody :: ' + responseBody + ' http status code :: ' + httpStatus);
			} catch(ex) {
				var message = ex.getMessage();
				current.errorinupload = true;
				current.errormessage = message;
			}
		}
		current.update();
	} else {
		gs.info('not attchemnts received');
	}

	gs.info('<business rule : Upload To GitHub> execution ends');

})(current, previous);
