sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// Add your code here
	gs.info('<business rule : Upload To GitHub> execution starts');
	var files = x_98418_github.getAttachments(current.sys_id);
	if(files.length > 0) {
		current.filescount = files.length;
		for(var i=0; i < files.length; i++) {

			var fileId = files[i].id;
			var fileName = files[i].name;
			var encContent = files[i].content;
			var path = "";
			if(current.folder) {
				path = x_98418_github.replaceSpaceWith_(current.folder+'/'+fileName);
			} else {
				path = x_98418_github.replaceSpaceWith_(fileName);
			}

			//call the rest web service to upload the file into github

			try {
				var r = new sn_ws.RESTMessageV2('x_98418_github.GitHub Create File', 'PUT');
				r.setStringParameterNoEscape('path', path);
				r.setStringParameterNoEscape('content', '"' + encContent + '"');
				r.setStringParameterNoEscape('userEmail', '"' + current.contributor + '"');
				r.setStringParameterNoEscape('userName', '"' + current.contributorname + '"');

				var response = r.execute();
				var responseBody = response.getBody();
				var httpStatus = response.getStatusCode();
				gs.info('ResponseBody :: ' + responseBody + ' http status code :: ' + httpStatus);
				if(responseBody) {
					responseBody =  JSON.parse(responseBody);
				}

				if(httpStatus === 201) {
					var file_urls = current.files;
					if(file_urls) {
						current.files = file_urls + ',' + decodeURI(responseBody.content.html_url);
					} else {
						current.files = decodeURI(responseBody.content.html_url);
					}
				} else {
					current.errorinupload = true;
					if(response.getErrorMessage()) {
						gs.info('response.getErrorMessage() :: ' + response.getErrorMessage());
						current.errormessage = response.getErrorMessage() + ' respone message ' + responseBody.message;
					} else {
						gs.info('parsed.messag :: ' + responseBody.message);
						current.errormessage = responseBody.message;
					}
					i = files.length; //stopping the loop in error case
				}
			} catch(ex) {
				var message = ex.getMessage();
				current.errorinupload = true;
				current.errormessage = message;
				i = files.length; //stopping the loop in error case
			}

		}
		current.update();
	} else {
		gs.info('not attchemnts received');
	}

	gs.info('<business rule : Upload To GitHub> execution ends');

})(current, previous);
