sys_script_include.script:
function getAttachments(docId) {
	gs.info('<script include : getAttachments> execution starts');
	var attachments = [];
	var gr = new GlideRecord('sys_attachment');
    gr.addQuery('table_sys_id',docId);
    gr.query();
    while (gr.next()) {
		gs.info('<script include : getAttachments> file name :: ' + gr.getValue('file_name') + ' id :: ' + gr.getValue('sys_id'));
		var attachmentApi = new GlideSysAttachment();
		var encData = attachmentApi.getContentBase64(gr);
		var file = { "id" : gr.getValue('sys_id'), "name" : gr.getValue('file_name'), "content" : encData };
        attachments.push(file);
    }
	gs.info('<script include : getAttachments> execution end');
	return attachments;
}
