sys_script_include.script:
function deleteAttachments(docId) {
	gs.info('<script include : deleteAttachments> execution starts');
	var gr = new GlideRecord('sys_attachment');
    gr.addQuery('table_sys_id',docId);
    gr.query();
    while (gr.next()) {
		gs.info('<script include : deleteAttachments> file name :: ' + gr.getValue('file_name') + ' id :: ' + gr.getValue('sys_id'));
		var attachmentApi = new GlideSysAttachment();
		attachmentApi.deleteAttachment(gr.getValue('sys_id'));
    }
	gs.info('<script include : deleteAttachments> execution end');
	return true;
}
