sysevent_in_email_action.script:
(function runAction(/*GlideRecord*/ current, /*GlideRecord*/ event, /*EmailWrapper*/ email, /*ScopedEmailLogger*/ logger, /*EmailClassifier*/ classifier) {

	// Implement email action here
	current.contributor = gs.getUserName();
	current.contributorname = gs.getUserDisplayName();
	var subject = email.subject;
	if(subject) { 
		if(subject.includes("fw") || subject.includes("fwd") || subject.includes("Fw") || subject.includes("Fwd")) { //to handle the forward mails
		  var index = subject.indexOf(":");
	      str = str.substr(index+1, subject.length);
		}
	}
    current.folder = subject.trim();
    current.insert();

})(current, event, email, logger, classifier);
